var payment_update=0;var do_payment_details=true;jQuery.extend(Drupal.settings,{ucShowProgressBar:false,ucOrderInitiate:false});jQuery(document).ready(function(){if(Drupal.settings.ucShowProgressBar){show_progressBar("#line-items-div");}if(Drupal.settings.ucOrderInitiate){get_payment_details(Drupal.settings.ucURL.adminOrders+jQuery("[name=order_id]").val()+"/payment_details/"+jQuery("#edit-payment-method").val());}});function show_progressBar(b){var a=new Drupal.progressBar("paymentProgress");a.setProgress(-1,"");jQuery(b).empty().append(a.element);}function init_payment_details(a){if(payment_update==0){get_payment_details(Drupal.settings.ucURL.checkoutPaymentDetails+a);}}function get_payment_details(d){var a=new Drupal.progressBar("paymentProgress");a.setProgress(-1,"");jQuery("#payment_details").empty().append(a.element).removeClass("display-none");var c=new Date();payment_update=c.getTime();var b;if(jQuery("#edit-payment-details-data").length){b={"payment-details-data":jQuery("#edit-payment-details-data").val()};
}else{b={};}jQuery.post(d,b,function(e){if(c.getTime()==payment_update){if(e==""){jQuery("#payment_details").empty().html(Drupal.settings.defPaymentMsg);}else{jQuery("#payment_details").empty().append(e);}}});}function cvv_info_popup(){var a=window.open(Drupal.settings.ucURL.creditCardCVVInfo,"CVV_Info","toolbar=0,scrollbars=1,location=0,statusbar=0,menubar=0,resizable=1,width=480,height=460,left=282,top=122");}function receive_check_toggle(a){if(!a){jQuery("#edit-amount").removeAttr("disabled").val("");jQuery("#edit-comment").removeAttr("disabled").val("");}else{jQuery("#edit-amount").attr("disabled","true").val("-");jQuery("#edit-comment").attr("disabled","true").val("-");}}