Drupal.behaviors.teaser=function(a){$("textarea.teaser:not(.teaser-processed)",a).each(function(){var f=$(this).addClass("teaser-processed");var i=$("#"+Drupal.settings.teaser[this.id]);var h=$("#"+Drupal.settings.teaserCheckbox[this.id]).parent();var j=$(h).children("input").attr("checked")?true:false;var k=f[0].parentNode;$(i).before(f);$(k).remove();function c(m){return m.replace(/^\s+/g,"").replace(/\s+$/g,"");}function e(){if(f.val()){i.val(c(f.val())+"\r\n\r\n"+c(i.val()));}f[0].value="";$(f).attr("disabled","disabled");$(f).parent().slideUp("fast");$(this).val(Drupal.t("Split summary at cursor"));$(h).hide();j=$(h).children("input").attr("checked")?true:false;$(h).children("input").attr("checked",true);}function d(){i[0].focus();var n=Drupal.getSelection(i[0]);var m=n.start;var o=i.val();f[0].value=c(o.slice(0,m));i[0].value=c(o.slice(m));$(f).attr("disabled","");$(f).parent().slideDown("fast");$(this).val(Drupal.t("Join summary"));$(h).show().children("input").attr("checked",j);
}var g=$('<div class="teaser-button-wrapper"><input type="button" class="teaser-button" /></div>');var b=$("#"+this.id.substring(0,this.id.length-2)+"include");$(b).parent().parent().before(g);var l=i.val().split("<!--break-->",2);if(l.length==2){f[0].value=c(l[0]);i[0].value=c(l[1]);$(f).attr("disabled","");$("input",g).val(Drupal.t("Join summary")).toggle(e,d);}else{$("input",g).val(Drupal.t("Split summary at cursor")).toggle(d,e);$(h).hide().children("input").attr("checked",true);}if(Drupal.behaviors.textarea&&f.is((".form-textarea:not(.textarea-processed)"))){Drupal.behaviors.textarea(f.parentNode);}if(f[0].disabled){f.parent().hide();}});};