(function(a){Drupal.behaviors.CToolsJumpMenu={attach:function(b){a(".ctools-jump-menu-hide:not(.ctools-jump-menu-processed)").addClass("ctools-jump-menu-processed").hide();a(".ctools-jump-menu-change:not(.ctools-jump-menu-processed)").addClass("ctools-jump-menu-processed").change(function(){var d=a(this).val();var c=d.split("::");if(c[1]){location.href=c[1];}else{location.href=d;}return false;});a(".ctools-jump-menu-button:not(.ctools-jump-menu-processed)").addClass("ctools-jump-menu-processed").click(function(){var d=a(this).parents("form").find(".ctools-jump-menu-select");var e=d.val();var c=e.split("::");if(c[1]){location.href=c[1];}else{location.href=e;}return false;});}};})(jQuery);