Drupal.behaviors.expAdminSurveyQuestionType={attach:function(a,b){$(".addedit-edit-question_type:not(.question_type-initialized)").addClass("question_type-initialized").each(function(){var c=$(this).val();if(c=="0"||c==0){$(this).removeClass("select-normal-text");$(this).addClass("select-greyed-out-text");}else{$(this).removeClass("select-greyed-out-text");$(this).addClass("select-normal-text");}if($(this).is(":disabled")){$(this).removeClass("select-normal-text");$(this).addClass("select-greyed-out-text");}else{$(this).removeClass("select-greyed-out-text");$(this).addClass("select-normal-text");}$(this).change(function(d){var e=$(this).val();if(e=="0"||e==0){$(this).removeClass("select-normal-text");$(this).addClass("select-greyed-out-text");}else{$(this).removeClass("select-greyed-out-text");$(this).addClass("select-normal-text");}});});}};