Drupal.wysiwyg.plugins.awesome={isNode:function(a){return($(a).is("img.mymodule-awesome"));},invoke:function(d,a,c){if(d.format=="html"){if($(d.node).is("img.mymodule-awesome")){return;}var b=this._getPlaceholder(a);}else{var b="<!--break-->";}if(typeof b!="undefined"){Drupal.wysiwyg.instances[c].insert(b);}},attach:function(c,a,b){c=c.replace(/<!--break-->/g,this._getPlaceholder(a));return c;},detach:function(d,b,c){var a=$("<div>"+d+"</div>");$.each($("img.mymodule-awesome",a),function(e,f){});return a.html();},_getPlaceholder:function(a){return'<img src="'+a.path+'/images/spacer.gif" alt="&lt;--break-&gt;" title="&lt;--break--&gt;" class="wysiwyg-break drupal-content" />';}};