function updateAccessOnKeyDown(evt,obj){
	try{
	evt = evt || window.event;
    var charCode = evt.keyCode || evt.which;
    if (charCode == 13) {           	    	
        evt.preventDefault();
        evt.stopPropagation();
        return false;           	    	
    }
	}catch(e){
		// to do
	}
}

function selectKeyDownAccess(id, evt, type){
	/* 	
		Down arrow - 40
		Up arrow - 38
	*/
	try{
	evt = evt || window.event;
    var charCode = evt.keyCode || evt.which;
    if (charCode == 40) { 
    	$('#'+type+' #'+id).next().focus();           	    	
    	$('#'+type+' #'+id).next().addClass('selected-list');
    }	
    if (charCode == 38) { 
    	$('#'+type+' #'+id).removeClass('selected-list');           	    	
    	$('#'+type+' #'+id).prev().focus();
    }	
    evt.preventDefault();
    evt.stopPropagation();
    return false;  
	}catch(e){
		// to do
	}
}
