function disableEnrterKey(evt){
	try{
	  var eve = evt || window.event;
	  var keycode = eve.keyCode || eve.which || eve.charCode;

	  if (keycode == 13) {
	    eve.cancelBubble = true;
	    eve.returnValue = false;

	    if (eve.stopPropagation) {
	      eve.stopPropagation();
	      eve.preventDefault();
	    }

	    return false;
	  }
	}catch(e){
			// To Do
		}
}

