	function RGBToHex(value) 
	{
	    var re = /\d+/g;
	    var matches = value.match(re);
	    for( var i = 0; i < matches.length; i++ ) {
	        matches[i] = parseInt(matches[i]).toString(16);
	        if( matches[i].length < 2 ) matches[i] = '0'+matches[i];
	    }
	    
		return "#" + matches[0] + matches[1] + matches[2];
	}



