        //alert('current modalBackdropWidth = ' + modalBackdropWidth);
        
        var newModalBackdropHeight = parseInt(newModalTop) + parseInt(newModalContentOuterHeight);
        //alert('newModalBackdropHeight = ' + newModalBackdropHeight);
