function getRootId(node, objTree){
	try{
	var par = objTree.parent(node);
	if(par==-1 || par==false){
		return $(node).attr("id");
	}
	else if($(par).attr("id")=="R")
		return $(node).attr("id");
	else if($(objTree.parent(par)).attr("id")=="R"){
		return $(par).attr("id");
	}
	else
		return getRootId(par,objTree);
	}catch(e){
		// to do
	}
}

